// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "./PredictionMarket.sol";
import "./WBNB3009.sol";

/**
 * @title X402BettingBNB
 * @dev x402 protocol for 100% GASLESS BNB betting
 * @notice Users only need BNB, NO separate USDC needed!
 * 
 * REVOLUTIONARY APPROACH:
 * 1. User wraps BNB → WBNB3009 (one-time, pays gas once)
 * 2. User signs WBNB3009 authorization (EIP-3009, off-chain, FREE)
 * 3. Facilitator executes (pays gas)
 * 4. WBNB3009 auto-unwraps to BNB
 * 5. BNB used for betting
 * 
 * RESULT: After initial wrap, ALL bets are 100% GASLESS!
 */
contract X402BettingBNB is ReentrancyGuard, Ownable {
    
    PredictionMarket public predictionMarket;
    WBNB3009 public wbnb;
    
    // x402 payment tracking
    mapping(bytes32 => bool) public usedNonces;
    mapping(address => uint256) public gasCredits; // Sponsored gas tracking
    
    // Facilitator settings
    uint256 public facilitatorFee = 50; // 0.5% (50 basis points)
    address public facilitator;
    
    event GaslessBetPlaced(
        uint256 indexed marketId,
        address indexed user,
        bool position,
        uint256 bnbAmount,
        bytes32 nonce
    );
    
    event GasSponsored(
        address indexed user,
        uint256 gasUsed
    );
    
    event WBNBWrapped(
        address indexed user,
        uint256 amount
    );
    
    constructor(
        address _predictionMarket,
        address _wbnb
    ) Ownable(msg.sender) {
        predictionMarket = PredictionMarket(payable(_predictionMarket));
        wbnb = WBNB3009(payable(_wbnb));
        facilitator = msg.sender;
    }
    
    /**
     * @dev Helper: Wrap BNB to WBNB3009
     * @notice Users can call this directly on WBNB contract or use this helper
     */
    function wrapBNB() external payable {
        wbnb.deposit{value: msg.value}();
        require(wbnb.transfer(msg.sender, msg.value), "Transfer failed");
        emit WBNBWrapped(msg.sender, msg.value);
    }
    
    /**
     * @dev GASLESS BETTING with pure BNB!
     * @notice User signs WBNB3009 authorization, facilitator executes
     * 
     * Flow:
     * 1. User has WBNB3009 balance
     * 2. User signs EIP-3009 authorization (off-chain, no gas)
     * 3. Facilitator calls this function (pays gas)
     * 4. WBNB3009 transferred to contract
     * 5. WBNB3009 unwrapped to BNB
     * 6. BNB sent to PredictionMarket for bet
     * 7. User paid ZERO gas!
     */
    function gaslessBetWithBNB(
        uint256 marketId,
        bool position,
        address from,
        uint256 wbnbValue,
        uint256 validAfter,
        uint256 validBefore,
        bytes32 nonce,
        bytes memory signature
    ) external nonReentrant {
        // Only facilitator can execute (gas sponsor)
        require(msg.sender == facilitator || msg.sender == owner(), "Only facilitator");
        
        // Check nonce not used
        require(!usedNonces[nonce], "Nonce already used");
        usedNonces[nonce] = true;
        
        uint256 gasStart = gasleft();
        
        // Calculate amounts
        uint256 feeAmount = (wbnbValue * facilitatorFee) / 10000;
        uint256 betAmount = wbnbValue - feeAmount;
        
        // Execute EIP-3009 transfer (WBNB3009 from user to contract)
        wbnb.transferWithAuthorization(
            from,
            address(this),
            wbnbValue,
            validAfter,
            validBefore,
            nonce,
            signature
        );
        
        // Unwrap WBNB3009 → BNB
        wbnb.withdraw(betAmount);
        
        // Place bet with native BNB
        predictionMarket.buyPositionForUser{value: betAmount}(
            marketId,
            position,
            from
        );
        
        // Track gas used (for analytics)
        uint256 gasUsed = (gasStart - gasleft()) * tx.gasprice;
        gasCredits[from] += gasUsed;
        
        emit GaslessBetPlaced(marketId, from, position, betAmount, nonce);
        emit GasSponsored(from, gasUsed);
    }
    
    /**
     * @dev Batch gasless bets (multiple bets in one tx)
     * @notice Even more efficient - spread gas cost across multiple bets!
     */
    function batchGaslessBets(
        uint256[] calldata marketIds,
        bool[] calldata positions,
        address[] calldata froms,
        uint256[] calldata wbnbValues,
        uint256[] calldata validAfters,
        uint256[] calldata validBefores,
        bytes32[] calldata nonces,
        bytes[] calldata signatures
    ) external nonReentrant {
        require(msg.sender == facilitator || msg.sender == owner(), "Only facilitator");
        require(
            marketIds.length == positions.length &&
            positions.length == froms.length &&
            froms.length == wbnbValues.length &&
            wbnbValues.length == nonces.length &&
            nonces.length == signatures.length,
            "Array length mismatch"
        );
        
        for (uint256 i = 0; i < marketIds.length; i++) {
            // Check nonce
            require(!usedNonces[nonces[i]], "Nonce already used");
            usedNonces[nonces[i]] = true;
            
            uint256 feeAmount = (wbnbValues[i] * facilitatorFee) / 10000;
            uint256 betAmount = wbnbValues[i] - feeAmount;
            
            // Transfer WBNB3009
            wbnb.transferWithAuthorization(
                froms[i],
                address(this),
                wbnbValues[i],
                validAfters[i],
                validBefores[i],
                nonces[i],
                signatures[i]
            );
            
            // Unwrap and bet
            wbnb.withdraw(betAmount);
            predictionMarket.buyPositionForUser{value: betAmount}(
                marketIds[i],
                positions[i],
                froms[i]
            );
            
            emit GaslessBetPlaced(marketIds[i], froms[i], positions[i], betAmount, nonces[i]);
        }
    }
    
    /**
     * @dev GASLESS CLAIM with EIP-712 signature
     * @notice User signs claim authorization, facilitator executes
     * User receives BNB directly, pays ZERO gas!
     */
    function gaslessClaim(
        uint256 marketId,
        address user,
        bytes32 nonce,
        bytes memory signature
    ) external nonReentrant {
        require(msg.sender == facilitator || msg.sender == owner(), "Only facilitator");
        require(!usedNonces[nonce], "Nonce already used");
        
        // TODO: Verify EIP-712 signature for claim authorization
        // For now, trust facilitator (in production, add signature verification)
        
        usedNonces[nonce] = true;
        
        // Claim winnings to this contract
        uint256 balanceBefore = address(this).balance;
        predictionMarket.claimWinnings(marketId);
        uint256 winnings = address(this).balance - balanceBefore;
        
        // Transfer winnings to user
        (bool success, ) = user.call{value: winnings}("");
        require(success, "Transfer failed");
    }
    
    /**
     * @dev Set facilitator address
     */
    function setFacilitator(address _facilitator) external onlyOwner {
        facilitator = _facilitator;
    }
    
    /**
     * @dev Set facilitator fee (in basis points, e.g. 50 = 0.5%)
     */
    function setFacilitatorFee(uint256 _fee) external onlyOwner {
        require(_fee <= 500, "Fee too high"); // Max 5%
        facilitatorFee = _fee;
    }
    
    /**
     * @dev Emergency withdraw BNB (only owner)
     */
    function emergencyWithdraw() external onlyOwner {
        (bool success, ) = owner().call{value: address(this).balance}("");
        require(success, "Withdrawal failed");
    }
    
    /**
     * @dev Receive BNB from WBNB unwrapping
     */
    receive() external payable {}
}

contracts/PredictionMarket.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "./TraderReputation.sol";

/**
 * @title PredictionMarket
 * @dev Decentralized prediction market on BNB Chain with AI-assisted oracle integration
 * @notice Built for Seedify Hackathon 2025 - Now with on-chain reputation and copy trading
 */
contract PredictionMarket is ReentrancyGuard, Ownable {
    struct Market {
        uint256 id;
        string question;
        string description;
        string category;
        address creator;
        uint256 endTime;
        uint256 totalYesAmount;
        uint256 totalNoAmount;
        bool resolved;
        bool outcome;
        uint256 resolvedAt;
        bool aiOracleEnabled;
    }

    struct Position {
        uint256 yesAmount;
        uint256 noAmount;
        bool claimed;
    }

    // State variables
    uint256 public marketCount;
    uint256 public constant PLATFORM_FEE = 200; // 2% fee (in basis points)
    uint256 public constant MIN_BET = 0.001 ether; // 0.001 BNB minimum bet
    uint256 public constant MIN_REPUTATION_TO_CREATE = 50; // Minimum reputation score to create markets
    
    TraderReputation public reputationContract;
    
    mapping(uint256 => Market) public markets;
    mapping(uint256 => mapping(address => Position)) public positions;
    mapping(address => bool) public authorizedOracles;
    
    // Events
    event MarketCreated(
        uint256 indexed marketId,
        string question,
        uint256 endTime,
        address indexed creator
    );
    
    event PositionTaken(
        uint256 indexed marketId,
        address indexed user,
        bool position,
        uint256 amount
    );
    
    event MarketResolved(
        uint256 indexed marketId,
        bool outcome,
        uint256 resolvedAt
    );
    
    event WinningsClaimed(
        uint256 indexed marketId,
        address indexed user,
        uint256 amount
    );

    constructor() Ownable(msg.sender) {
        // Initialize with deployer as authorized oracle
        authorizedOracles[msg.sender] = true;
        
        // Deploy reputation contract with this contract as owner
        reputationContract = new TraderReputation(address(this));
    }
    
    /**
     * @dev Set reputation contract address (for existing deployments)
     */
    function setReputationContract(address _reputationContract) external onlyOwner {
        reputationContract = TraderReputation(_reputationContract);
    }

    /**
     * @dev Create a new prediction market
     */
    function createMarket(
        string memory _question,
        string memory _description,
        string memory _category,
        uint256 _endTime,
        bool _aiOracleEnabled
    ) external returns (uint256) {
        require(_endTime > block.timestamp, "End time must be in the future");
        require(bytes(_question).length > 0, "Question cannot be empty");
        
        // Check reputation requirement (owner bypasses this)
        if (address(reputationContract) != address(0) && msg.sender != owner()) {
            uint256 reputation = reputationContract.getReputationScore(msg.sender);
            require(reputation >= MIN_REPUTATION_TO_CREATE, "Insufficient reputation to create markets. Place predictions to earn reputation.");
        }

        marketCount++;
        uint256 marketId = marketCount;

        markets[marketId] = Market({
            id: marketId,
            question: _question,
            description: _description,
            category: _category,
            creator: msg.sender,
            endTime: _endTime,
            totalYesAmount: 0,
            totalNoAmount: 0,
            resolved: false,
            outcome: false,
            resolvedAt: 0,
            aiOracleEnabled: _aiOracleEnabled
        });

        emit MarketCreated(marketId, _question, _endTime, msg.sender);
        return marketId;
    }

    /**
     * @dev Buy a position in a market (YES or NO)
     */
    function buyPosition(uint256 _marketId, bool _position) 
        external 
        payable 
        nonReentrant 
    {
        _buyPositionInternal(_marketId, _position, msg.sender, msg.value);
    }
    
    /**
     * @dev Buy position for user (called by x402 facilitator for gasless betting)
     */
    function buyPositionForUser(
        uint256 _marketId,
        bool _position,
        address user
    ) external payable nonReentrant {
        // Only authorized x402 contracts can call this
        require(authorizedOracles[msg.sender], "Not authorized");
        _buyPositionInternal(_marketId, _position, user, msg.value);
    }
    
    /**
     * @dev Internal function to buy position (supports both regular and gasless)
     */
    function _buyPositionInternal(
        uint256 _marketId,
        bool _position,
        address user,
        uint256 amount
    ) internal {
        Market storage market = markets[_marketId];
        require(market.id != 0, "Market does not exist");
        require(!market.resolved, "Market already resolved");
        require(block.timestamp < market.endTime, "Market has ended");
        require(amount >= MIN_BET, "Bet amount too low");

        Position storage userPosition = positions[_marketId][user];

        if (_position) {
            userPosition.yesAmount += amount;
            market.totalYesAmount += amount;
        } else {
            userPosition.noAmount += amount;
            market.totalNoAmount += amount;
        }

        emit PositionTaken(_marketId, user, _position, amount);
        
        // Record bet in reputation contract
        if (address(reputationContract) != address(0)) {
            reputationContract.recordBet(user, _marketId, amount, _position);
            
            // Check for copy traders and execute copy trades
            _executeCopyTrades(_marketId, user, _position, amount);
        }
    }
    
    /**
     * @dev Execute copy trades for followers
     */
    function _executeCopyTrades(
        uint256 _marketId,
        address trader,
        bool _position,
        uint256 amount
    ) internal {
        address[] memory traderFollowers = reputationContract.getFollowers(trader);
        
        for (uint256 i = 0; i < traderFollowers.length; i++) {
            address follower = traderFollowers[i];
            
            // Get copy trade settings
            (
                uint256 maxAmountPerTrade,
                uint256 copyPercentage,
                bool active,
                ,
                
            ) = reputationContract.getCopyTradeSettings(follower, trader);
            
            if (!active) continue;
            
            // Calculate copy amount
            uint256 copyAmount = (amount * copyPercentage) / 100;
            if (copyAmount > maxAmountPerTrade) {
                copyAmount = maxAmountPerTrade;
            }
            
            // Check follower has enough balance (simplified - in production use escrow)
            if (copyAmount < MIN_BET) continue;
            
            // Record copy trade position
            Position storage followerPosition = positions[_marketId][follower];
            if (_position) {
                followerPosition.yesAmount += copyAmount;
                markets[_marketId].totalYesAmount += copyAmount;
            } else {
                followerPosition.noAmount += copyAmount;
                markets[_marketId].totalNoAmount += copyAmount;
            }
            
            // Record in reputation contract
            reputationContract.recordCopyTrade(follower, trader, _marketId, copyAmount);
        }
    }

    /**
     * @dev Resolve a market (only authorized oracles)
     */
    function resolveMarket(uint256 _marketId, bool _outcome) 
        external 
        nonReentrant 
    {
        require(authorizedOracles[msg.sender], "Not authorized oracle");
        Market storage market = markets[_marketId];
        require(market.id != 0, "Market does not exist");
        require(!market.resolved, "Market already resolved");
        require(block.timestamp >= market.endTime, "Market has not ended");

        market.resolved = true;
        market.outcome = _outcome;
        market.resolvedAt = block.timestamp;

        emit MarketResolved(_marketId, _outcome, block.timestamp);
        
        // Update reputation for all participants
        if (address(reputationContract) != address(0)) {
            _updateParticipantReputation(_marketId, _outcome);
        }
    }
    
    /**
     * @dev Update reputation for all market participants after resolution
     * @dev Note: In production, process this in batches or off-chain to avoid gas limits
     * @dev For this implementation, reputation updates happen on claim instead
     */
    function _updateParticipantReputation(uint256 /* _marketId */, bool /* outcome */) internal pure {
        // Placeholder - reputation updates happen on claimWinnings instead
        // This prevents hitting gas limits when processing large markets
    }

    /**
     * @dev Claim winnings from a resolved market
     */
    function claimWinnings(uint256 _marketId) external nonReentrant {
        Market storage market = markets[_marketId];
        require(market.resolved, "Market not resolved");
        
        Position storage userPosition = positions[_marketId][msg.sender];
        require(!userPosition.claimed, "Already claimed");
        
        uint256 winningAmount = calculateWinnings(_marketId, msg.sender);
        require(winningAmount > 0, "No winnings to claim");
        
        userPosition.claimed = true;
        
        // Deduct platform fee
        uint256 fee = (winningAmount * PLATFORM_FEE) / 10000;
        uint256 payout = winningAmount - fee;
        
        // Determine if user won or lost
        bool won = false;
        uint256 userBet = 0;
        if (market.outcome) {
            userBet = userPosition.yesAmount;
            won = userBet > 0;
        } else {
            userBet = userPosition.noAmount;
            won = userBet > 0;
        }
        
        // Update reputation
        if (address(reputationContract) != address(0) && userBet > 0) {
            uint256 profit = won ? (payout > userBet ? payout - userBet : 0) : 0;
            reputationContract.settleBet(msg.sender, _marketId, won, profit);
        }
        
        (bool success, ) = msg.sender.call{value: payout}("");
        require(success, "Transfer failed");
        
        emit WinningsClaimed(_marketId, msg.sender, payout);
    }

    /**
     * @dev Calculate potential winnings for a user
     */
    function calculateWinnings(uint256 _marketId, address _user) 
        public 
        view 
        returns (uint256) 
    {
        Market storage market = markets[_marketId];
        if (!market.resolved) return 0;
        
        Position storage userPosition = positions[_marketId][_user];
        if (userPosition.claimed) return 0;
        
        uint256 userBet;
        uint256 totalWinningBets;
        uint256 totalLosingBets;
        
        if (market.outcome) {
            // YES won
            userBet = userPosition.yesAmount;
            totalWinningBets = market.totalYesAmount;
            totalLosingBets = market.totalNoAmount;
        } else {
            // NO won
            userBet = userPosition.noAmount;
            totalWinningBets = market.totalNoAmount;
            totalLosingBets = market.totalYesAmount;
        }
        
        if (userBet == 0 || totalWinningBets == 0) return 0;
        
        // Calculate proportional share of losing bets
        uint256 share = (userBet * totalLosingBets) / totalWinningBets;
        return userBet + share;
    }

    /**
     * @dev Get market odds (in percentage)
     */
    function getMarketOdds(uint256 _marketId) 
        external 
        view 
        returns (uint256 yesOdds, uint256 noOdds) 
    {
        Market storage market = markets[_marketId];
        uint256 total = market.totalYesAmount + market.totalNoAmount;
        
        if (total == 0) {
            return (5000, 5000); // 50/50 if no bets
        }
        
        yesOdds = (market.totalYesAmount * 10000) / total;
        noOdds = (market.totalNoAmount * 10000) / total;
    }

    /**
     * @dev Add or remove authorized oracle
     */
    function setAuthorizedOracle(address _oracle, bool _authorized) 
        external 
        onlyOwner 
    {
        authorizedOracles[_oracle] = _authorized;
    }

    /**
     * @dev Get all market IDs
     */
    function getAllMarkets() external view returns (uint256[] memory) {
        uint256[] memory marketIds = new uint256[](marketCount);
        for (uint256 i = 1; i <= marketCount; i++) {
            marketIds[i - 1] = i;
        }
        return marketIds;
    }

    /**
     * @dev Get user position in a market
     */
    function getUserPosition(uint256 _marketId, address _user) 
        external 
        view 
        returns (uint256 yesAmount, uint256 noAmount, bool claimed) 
    {
        Position storage pos = positions[_marketId][_user];
        return (pos.yesAmount, pos.noAmount, pos.claimed);
    }

    /**
     * @dev Withdraw platform fees (only owner)
     */
    function withdrawFees() external onlyOwner {
        uint256 balance = address(this).balance;
        require(balance > 0, "No fees to withdraw");
        (bool success, ) = owner().call{value: balance}("");
        require(success, "Withdrawal failed");
    }

    receive() external payable {}
}

contracts/WBNB3009.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";

/**
 * @title WBNB3009
 * @dev Wrapped BNB with EIP-3009 transferWithAuthorization for x402 gasless transactions
 * @notice This allows users to interact with BNB in a gasless way!
 * 
 * How it works:
 * 1. User wraps BNB → gets WBNB3009 tokens
 * 2. User signs EIP-3009 authorization (off-chain, FREE)
 * 3. Facilitator executes authorization (pays gas)
 * 4. WBNB3009 transferred to contract
 * 5. Contract unwraps to native BNB
 * 6. BNB used for betting
 * 
 * Result: User only needs BNB once (to wrap), then ALL future transactions are gasless!
 */
contract WBNB3009 is ERC20, ReentrancyGuard {
    
    // EIP-712 Domain
    bytes32 public DOMAIN_SEPARATOR;
    
    // EIP-3009 typehashes
    bytes32 public constant TRANSFER_WITH_AUTHORIZATION_TYPEHASH = 
        keccak256("TransferWithAuthorization(address from,address to,uint256 value,uint256 validAfter,uint256 validBefore,bytes32 nonce)");
    
    bytes32 public constant RECEIVE_WITH_AUTHORIZATION_TYPEHASH =
        keccak256("ReceiveWithAuthorization(address from,address to,uint256 value,uint256 validAfter,uint256 validBefore,bytes32 nonce)");
    
    // Nonce tracking for replay protection
    mapping(address => mapping(bytes32 => bool)) public authorizationState;
    
    // Events
    event Deposit(address indexed account, uint256 amount);
    event Withdrawal(address indexed account, uint256 amount);
    event AuthorizationUsed(address indexed authorizer, bytes32 indexed nonce);
    
    constructor() ERC20("Wrapped BNB with x402", "WBNB3009") {
        // Create EIP-712 domain separator
        DOMAIN_SEPARATOR = keccak256(
            abi.encode(
                keccak256("EIP712Domain(string name,string version,uint256 chainId,address verifyingContract)"),
                keccak256(bytes("Wrapped BNB with x402")),
                keccak256(bytes("1")),
                block.chainid,
                address(this)
            )
        );
    }
    
    /**
     * @dev Wrap BNB to WBNB3009 (1:1)
     */
    function deposit() external payable {
        _mint(msg.sender, msg.value);
        emit Deposit(msg.sender, msg.value);
    }
    
    /**
     * @dev Unwrap WBNB3009 back to BNB (1:1)
     */
    function withdraw(uint256 amount) external nonReentrant {
        _burn(msg.sender, amount);
        (bool success, ) = msg.sender.call{value: amount}("");
        require(success, "BNB transfer failed");
        emit Withdrawal(msg.sender, amount);
    }
    
    /**
     * @dev Allow contract to receive BNB for wrapping
     */
    receive() external payable {
        _mint(msg.sender, msg.value);
        emit Deposit(msg.sender, msg.value);
    }
    
    /**
     * @dev EIP-3009 transferWithAuthorization
     * @notice This is the KEY function for gasless x402 transactions!
     * User signs this off-chain, facilitator executes and pays gas
     */
    function transferWithAuthorization(
        address from,
        address to,
        uint256 value,
        uint256 validAfter,
        uint256 validBefore,
        bytes32 nonce,
        bytes memory signature
    ) external nonReentrant {
        // Check time validity
        require(block.timestamp >= validAfter, "Authorization not yet valid");
        require(block.timestamp <= validBefore, "Authorization expired");
        
        // Check nonce not used (replay protection)
        require(!authorizationState[from][nonce], "Authorization already used");
        
        // Verify EIP-712 signature
        bytes32 digest = keccak256(
            abi.encodePacked(
                "\x19\x01",
                DOMAIN_SEPARATOR,
                keccak256(
                    abi.encode(
                        TRANSFER_WITH_AUTHORIZATION_TYPEHASH,
                        from,
                        to,
                        value,
                        validAfter,
                        validBefore,
                        nonce
                    )
                )
            )
        );
        
        address signer = _recoverSigner(digest, signature);
        require(signer == from, "Invalid signature");
        
        // Mark nonce as used
        authorizationState[from][nonce] = true;
        emit AuthorizationUsed(from, nonce);
        
        // Execute transfer
        _transfer(from, to, value);
    }
    
    /**
     * @dev EIP-3009 receiveWithAuthorization
     * @notice Safer version that ensures msg.sender is the recipient
     * Prevents front-running attacks
     */
    function receiveWithAuthorization(
        address from,
        address to,
        uint256 value,
        uint256 validAfter,
        uint256 validBefore,
        bytes32 nonce,
        bytes memory signature
    ) external nonReentrant {
        // IMPORTANT: msg.sender must be the recipient (prevents front-running)
        require(to == msg.sender, "Caller must be the payee");
        
        // Check time validity
        require(block.timestamp >= validAfter, "Authorization not yet valid");
        require(block.timestamp <= validBefore, "Authorization expired");
        
        // Check nonce not used
        require(!authorizationState[from][nonce], "Authorization already used");
        
        // Verify EIP-712 signature
        bytes32 digest = keccak256(
            abi.encodePacked(
                "\x19\x01",
                DOMAIN_SEPARATOR,
                keccak256(
                    abi.encode(
                        RECEIVE_WITH_AUTHORIZATION_TYPEHASH,
                        from,
                        to,
                        value,
                        validAfter,
                        validBefore,
                        nonce
                    )
                )
            )
        );
        
        address signer = _recoverSigner(digest, signature);
        require(signer == from, "Invalid signature");
        
        // Mark nonce as used
        authorizationState[from][nonce] = true;
        emit AuthorizationUsed(from, nonce);
        
        // Execute transfer
        _transfer(from, to, value);
    }
    
    /**
     * @dev Recover signer from signature
     */
